import { MachineConfig, send, Action, assign, actions } from "xstate";


export function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

export function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}



function Game(answer: any, RecResult: string){
    let score = 0;
    let resArray = RecResult.split(" ");
    resArray.forEach( word => 
        {if (word == answer.article) {(score += 0.25)}
        else if (word == answer.sv) {score += 0.75}
        })
        return(score)
}

const animals_list = ['cat', 'dog', 'fox', 'sheep', 'giraffe', 'crocodile', 'peacock' ]

function invoke_rasa(go_to_state: string,): MachineConfig<SDSContext, any, SDSEvent> {
    return ({ 
            invoke: {
                id: 'rasaApi',
                src: (context, event) =>  nluRequest(context.task),
                onDone:{
                    target: go_to_state,
                    actions:[
                        assign((context, event) => { return { intentResult: event.data.intent.name} }),
                        (context:SDSContext, event:any) => console.log(event.data)]
                },
                onError:{
                    target: '#choose_game',
                    actions: (context, event) => console.log(event.data)

                }

            }

        }  )}


function sayAskTimeout(say_this: Action<SDSContext, SDSEvent>): MachineConfig<SDSContext, any, SDSEvent> {
    return ({ 
        initial: 'prompt',
        states: {
            prompt: {
                entry: say_this,

                on: { ENDSPEECH: 'ask' }
            },
            ask: {
                entry: [send('LISTEN'), send ('TIMEOUT', {delay: 3500})]
            },
            nomatch: {
                entry: say('Sorry, please repeat.'),
                on: {ENDSPEECH: "ask"}
            },
            timeout_final: {
                entry: say('Returning to idle.'),
                on: {ENDSPEECH: "idle"}
            },
            idle:{
                type: 'final'
            },
            timeout: {
                initial: 'prompt',
                on: {TIMEOUT: [{cond: (context) => context.count ===1, target: 'reprompt0'},
                                {cond: (context) => context.count ===2, target: 'reprompt1'},
                                {cond: (context) => context.count ===3, target: 'reprompt2'},
                                {cond: (context) => context.count ===4, target: 'idle'}]},
                states:{
                    prompt: {
                        entry: send('TIMEOUT')
                    }
                }
            },
            reprompt0: {
                entry: say('Why are you silent gaidy'),
                on: {ENDSPEECH: 'ask'}
            },
            reprompt1: {
                entry: say('Pyderis'),
                on: {ENDSPEECH: 'ask'}
            },
            reprompt2: {
                entry: say('Sorry Im a mess today. Hmmmm'),
                on: {ENDSPEECH: 'ask'}
            },
            to_do:{
                entry: say('Sorry, this tool has not been developed yet. I will now return to idle state.'),
                on: {ENDSPEECH: 'idle'}
            },
            canceled:{
                entry: say("Your appointment has been canceled. I will now returnto idle state.")
            },
        }
    })
}

const proxyurl = "https://cors-anywhere.herokuapp.com/";
const rasaurl = 'https://swenglish.herokuapp.com/model/parse'
const nluRequest = (text: string) =>
    fetch(new Request(proxyurl + rasaurl, {
