import { MachineConfig, send, Action, assign, actions } from "xstate";
import { mapContext } from "xstate/lib/utils";
import {game_vocab} from "./definitions_and_words"

const promptReset: Action<SDSContext, SDSEvent> = assign((context) => { return { count:0} })

export function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}
 
export function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}

function invoke_rasa(go_to_state: string,): MachineConfig<SDSContext, any, SDSEvent> {
    return ({ 
            invoke: {
                id: 'rasaApi',
                src: (context, event) =>  nluRequest(context.task),
                onDone:{
                    target: go_to_state,
                    actions:[
                        assign((context, event) => { return { intentResult: event.data.intent.name} }),
                        (context:SDSContext, event:any) => console.log(event.data)]
                },
                onError:{
                    target: '#welcome',
                    actions: (context, event) => console.log(event.data)

                }

            }

        }  )}


function sayAskTimeout(say_this: Action<SDSContext, SDSEvent>): MachineConfig<SDSContext, any, SDSEvent> {
    return ({ 
        initial: 'prompt',
        states: {
            prompt: {
                entry: [promptReset, say_this],
                on: { ENDSPEECH: 'ask'}},
            ask: {
                entry: [send('LISTEN'), send ('TIMEOUT', {delay: 15000})]
            },
            nomatch: {
                entry: say('Sorry, please repeat.'),
                on: {ENDSPEECH: "ask"}
            },
            timeout_final: {
                entry: say('Returning to idle.'),
                on: {ENDSPEECH: "idle"}
            },
            idle:{
                type: 'final'
            },
            timeout: {
                initial: 'prompt',
                on: {TIMEOUT: [{cond: (context) => context.count ===1, target: 'reprompt0'},
                                {cond: (context) => context.count ===2, target: 'reprompt1'},
                                {cond: (context) => context.count ===3, target: 'reprompt2'},
                                {cond: (context) => context.count ===4, target: 'timeout_final'}]},
                states:{
                    prompt: {
                        entry: send('TIMEOUT')
                    }
                }
            },
            reprompt0: {
                entry: say('Why are you silent'),
                on: {ENDSPEECH: 'ask'}
            },
            reprompt1: {
                entry: say('Not working'),
                on: {ENDSPEECH: 'ask'}
            },
            reprompt2: {
                entry: say('Sorry Im a mess today. Hmmmm'),
                on: {ENDSPEECH: 'ask'}
            },
        }
    })
}


function Say_play(guessWord: string): MachineConfig<SDSContext, any, SDSEvent> {
    return ({ 
        initial: 'prompt',
        states: {
            prompt: {
                entry: [say(guessWord), promptReset],
                on: {ENDSPEECH: 'ask'}
            },
            ask: {
                entry: [send('LISTEN'), send ('TIMEOUT', {delay: 10000})]
            },
            nomatch: {
                entry: say('Sorry, please repeat.'),
                on: {ENDSPEECH: "ask"}
            },
            timeout_final: {
                entry: say('Returning to idle.'),
                on: {ENDSPEECH: "idle"}
            },
            idle:{
                type: 'final'
            },
            timeout: {
                initial: 'prompt',
                on: {TIMEOUT: [{cond: (context) => context.count ===1, target: 'reprompt0'},
                                {cond: (context) => context.count ===2, target: 'reprompt0'},
                                {cond: (context) => context.count ===3, target: 'reprompt0'},
                                {cond: (context) => context.count ===4, target: 'reprompt0'}]},
                states:{
                    prompt: {
                        entry: send('TIMEOUT')
                    }
                }
            },
            reprompt0: {
                entry: [say("Sorry I'm a mess today. Hmmmm")],
                on: {ENDSPEECH: 'ask'}
            },
            almost:{
                entry: say("Right! But what is the correct article?"),
                on:{ENDSPEECH: 'ask'}
            },
            no_match: {
                entry: say('Try again'),
                on:{ENDSPEECH: 'ask'}
            },
            match: {
                entry: say('Correct!'),
                on: {ENDSPEECH:'go_to_next'}
            },
            go_to_next:{
                entry: send('NEXT_STATE')
            },
/*            reprompt1: {
                entry: say('Pyderis'),
                on: {ENDSPEECH: 'ask'}
            },
            reprompt2: {
                entry: say('Sorry Im a mess today. Hmmmm'),
                on: {ENDSPEECH: 'ask'}
            },*/

