const promptReset = assign(context => {
  return {
    count: 0
  };
});
export function say(text) {
  return send(_context => ({
    type: "SPEAK",
    value: text
  }));
}
export function listen() {
  return send('LISTEN');
}
const cancel = actions.cancel;

function invoke_rasa(go_to_state) {
  return {
    invoke: {
      id: 'rasaApi',
      src: (context, event) => nluRequest(context.task),
      onDone: {
        target: go_to_state,
        actions: [assign((context, event) => {
          return {
            intentResult: event.data.intent.name
          };
        }), (context, event) => console.log(event.data)]
      },
      onError: {
        target: '#welcome',
        actions: (context, event) => console.log(event.data)
      }
    }
  };
}

function sayAskTimeout(say_this) {
  return {
    initial: 'prompt',
    states: {
      prompt: {
        entry: [promptReset, say_this],
        on: {
          ENDSPEECH: 'ask'
        }
      },
      ask: {
        entry: [send('LISTEN'), send('TIMEOUT', {
          delay: 20000,
          id: 'timer'
        })]
      },
      nomatch: {
        entry: say('Sorry, please repeat.'),
        on: {
          ENDSPEECH: "ask"
        }
      },
      timeout_final: {
        entry: say('Returning to idle.'),
        on: {
          ENDSPEECH: "idle"
        }
      },
      idle: {
        id: 'idle',
        type: 'final'
      },
      timeout: {
        initial: 'prompt',
        on: {
          TIMEOUT: [{
