import { MachineConfig, send, Action, assign, actions } from "xstate";


export function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

export function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}



function Game(answer: any, RecResult: string){
    let score = 0;
    let resArray = RecResult.split(" ");
    resArray.forEach( word => 
        {if (word == answer.article) {(score += 0.25)}
        else if (word == answer.sv) {score += 0.75}
        })
        return(score)
}

const animals_list = ['cat', 'dog']

function invoke_rasa(go_to_state: string,): MachineConfig<SDSContext, any, SDSEvent> {
    return ({ 
            invoke: {
                id: 'rasaApi',
                src: (context, event) =>  nluRequest(context.task),
                onDone:{
                    target: go_to_state,
                    actions:[
                        assign((context, event) => { return { intentResult: event.data.intent.name} }),
                        (context:SDSContext, event:any) => console.log(event.data)]
                },
                onError:{
                    target: 'choose_game',
                    actions: (context, event) => console.log(event.data)

                }

            }

        }  )}


function say_ask_nomatch_timeout(say_this: Action<SDSContext, SDSEvent>, help_message: string): MachineConfig<SDSContext, any, SDSEvent> {
    return ({ 
        initial: 'prompt',
        states: {
            prompt: {
                entry: say_this,

                on: { ENDSPEECH: 'ask1' }
            },
            ask1: {
                entry: [send('LISTEN'), send ('TIMEOUT1', {delay: 4500, id: 'time1'})]
            },
            ask2: {
                entry: [send('LISTEN'), send ('TIMEOUT2', {delay: 4500, id: 'time2'})]
            },
            ask3: {
                entry: [send('LISTEN'), send ('TIMEOUT3', {delay: 4500, id: 'time3'})]
            },
            ask4: {
                entry: [send('LISTEN'), send ('TIMEOUT4', {delay: 4500, id: 'time4'})]
            },
            nomatch: {
                entry: say('Sorry, I did not manage to understand this one. Please repeat.'),
                on: {ENDSPEECH: "ask2"}
            },
            timeout1: {
                entry: say('I did not hear you this time, please speak louder.'),
                on: {ENDSPEECH: "ask2"}
            },
            timeout2: {
                entry: say('I still cannot hear you, please try again.'),
                on: {ENDSPEECH: "ask3"}
            },
            timeout3: {
                entry: say('Sorry, still nothing.'),
                on: {ENDSPEECH: "ask4"}
            },
            timeout4: {
                //Add idle state after 3 reprompts//
                entry: say('Returning to idle.'),
                on: {ENDSPEECH: "idle"}
            },
            idle:{
                type: 'final'
            },
            to_do:{
                entry: say('Sorry, this tool has not been developed yet. I will now return to idle state.'),
                on: {ENDSPEECH: 'idle'}
            },
            specific:{
                entry: say("Please be more specific"),
                on: {ENDSPEECH: "ask2"}
            },
            canceled:{
                entry: say("Your appointment has been canceled. I will now returnto idle state.")
            },
            help:{
                entry: say(help_message),
                on: {ENDSPEECH: "ask2"}
            }
        }
    })
}

const proxyurl = "https://cors-anywhere.herokuapp.com/";
const rasaurl = 'https://swenglish.herokuapp.com/model/parse'
const nluRequest = (text: string) =>
    fetch(new Request(proxyurl + rasaurl, {
        method: 'POST',
        headers: { 'Origin': 'http://maraev.me' }, // only required with proxy
        body: `{"text": "${text}"}`
    }))
        .then(data => data.json()); 

const grammar: { [index: string]: {help?: string } } = {

    "help": {help: "help"},
    "help me": {help: "help"},
    "Help": {help: "help"},
    "Help me": {help: "help"},
}

const boolean_grammar: {[index: string]: {agreement?: boolean, disagreement?: boolean, uncertain?: string}} = {
    "yes": {agreement: Boolean(true)},
    "of course": {agreement: Boolean(true)},
    "sure": {agreement: Boolean(true)},
    "absolutely": {agreement: Boolean(true)},
    "yes please": {agreement: Boolean(true)},
    "no": {disagreement: Boolean(false)},
    "no way": {disagreement: Boolean(false)},
    "absolutely not": {disagreement: Boolean(false)},
    "never": {disagreement: Boolean(false)},
    "maybe": {uncertain: "unsure"},
    "perhaps": {uncertain: "unsure"},
    "I don't know": {uncertain: "unsure"},
    "probably": {uncertain: "unsure"},
}

const {cancel} = actions

export const dmMachine: MachineConfig<SDSContext, any, SDSEvent> = ({
    
    initial: 'start_point',
    states: {
        start_point:{
            id: 'starting_poing',
            on: {
                CLICK: 'welcome'
            }
        },
        welcome: {
            initial: 'prompt',
            on: {ENDSPEECH: 'choose_game'},
            states:{
                prompt:{
                    entry: say("Welcome to the Sweenglish app.")
                }
            }
        },
        choose_game:{
            id: 'choose_game',
            initial: "prompt",
            on: {
                RECOGNISED:[{
                    actions: assign((context) => { return { task: context.recResult } }),
                    target: 'choice'},
                
                {target: ".nomatch"}]
            },
            states:{
                prompt: {
                    entry: say("Which game would you like to play?"),
                        on: {ENDSPEECH: 'ask'}
                },
                ask: {
                    entry: listen()
                },
                nomatch: { entry: say("Sorry, could you repeat?"),
                on: {ENDSPEECH: 'ask'}}
            }
        },
        choice:{
            initial: 'prompt',
            states:{
                prompt: { ...invoke_rasa('#tool')}
            }
        },
        tool:{
            initial: 'prompt',
            id: 'tool',
            on:{
                ENDSPEECH:[{
                    cond: (context) => context.intentResult === 'Definitions',
                    target: 'definitions'},
                    {cond: (context) => context.intentResult === 'Words',
                    target: 'wordplay'},
                    {cond: (context) => context.intentResult === 'Quit',
                    target: 'cancel'},
                {target:'.nomatch'}]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `${context.task}.`}))},
                nomatch: {
                    entry: say("Unavailable right now."),
                    on: {ENDSPEECH: '#choose_game'}
                        
                    }
                        
                }

        },
        wordplay: {
            initial: 'prompt',
            on: {ENDSPEECH: 'game_wordplay'},
            states:{
                prompt:{
                    entry: say("Welcome to the wordplay game! Say 'start' if you wish to start, help if you want me to go through the instructions and ca                    ")
                }
            }
        },
            game_wordplay:{
                initial: 'prompt',
                on:{ROUND: '#choose_game'},
                states: {
                    prompt: {
                        entry: say('hmm')
                        
                    }

                }
            },
        definitions: {
            initial: 'prompt',
            on: {ENDSPEECH: '#choose_game'},
            states:{
                prompt:{
                    entry: say("Welcome to the definitions game! Going back.")
                }
            }
        },
        cancel:{
            initial: 'prompt',
            on:{ ENDSPEECH: '#choose_game'
            },
            states:{
                prompt:{
                    entry: say("Cancelling.")
                }
            }
            },
        }
})
